mapper.select:
SELECT s.id AS id, s.name AS name, s.sex AS sex, s.age AS age, s.mobile AS mobile, s.email AS email, s.registerTime AS registerTime, s.classId AS classId, c.name AS className, d.name AS departmentName FROM student s JOIN class c ON s.classId = c.id JOIN department d ON c.departmentId = d.id
s.id != 1
AND s.name = #{name}
AND s.classId = #{classId}
LIMIT #{page.index}, #{page.pageSize}

mapper.update:
UPDATE
    student
SET
    password = #{password}
WHERE
    id = #{id}

UPDATE
    student
SET
    password = #{password}
WHERE
    email = #{email}

mapper.select:
SELECT COUNT(id) FROM student
id != 1
AND `name` = #{name}
AND classId = #{classId}
